//Default floor
$floor = 0;
for($i = 0; $i < strlen($instructions); $i++){
	$char = substr($instructions,$i,1);
	switch(ord($char)){
		case 40:
			$floor++;
			break;
		case 41:
			$floor--;
			break;
		default:
			$floor = $floor;
			break;
	}
}
echo "Santa is now on floor " . $floor."\n";
